public class Multime {    private int[] date;    private int dim;    private int n;    public Multime(){        this.date = new int[dim];        this.n = 0;    }    public Multime(int dim){        this.date = new int[dim];        this.dim = dim;        this.n = 0;    }    public void adauga(int elem){        if (n != dim){            boolean found = false;            for (int i = 0; i < dim; i++) {                if (date[i] == elem){                    found = true;                }            }            if (found == false){                n++;                date[n-1] = elem;            }        } else {            System.out.println("Vectorul este plin.");        }    }    public void extrage(int elem){        for (int i = 0; i < dim ; i++) {            if (date[i] == elem){                for (int j = i; j < dim - 1; j++){                    date[j] = date[j + 1];                }                n--;            }        }    }    public void afisare(){        for (int i = 0; i < dim; i++) {            System.out.print(date[i] + " ");        }        System.out.println();        System.out.println("Nr de elemente = " + n);    }}